export default function Main()
{
    const [user,setUser] = useState(auth.user)
    useEffect(() => {
    }, [user])
    console.log(auth.user)
    if(!auth.user)
        return (
            <MuiThemeProvider theme={theme}>
                <Login onLogin={setUser} />
            </MuiThemeProvider>
        )
    else
        return (<App />)
}
