import React,{useState,useEffect} from 'react';
import { CardContent,Card,Box,Typography } from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles'
const useStyles = makeStyles({
    card: {
      minWidth: 275,
      margin:16
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
    root:
      {
        display:'flex',
        flexWrap:'wrap',
        flexDirection:'column',
        alignItems:'center',
      }
  });
export default function InternApp(props)
{
    const [state,setState] = useState();

    useEffect(
      ()=>async function fetchData()
      {
        const response = await fetch('/api/interns/'+props.intern.id)
        const body = await response.json();
        console.log(body)
      },
      [state]
    )
    console.log(props.intern)
    const classes = useStyles();
    return(
      <Box className = {classes.root}>
        {
         props.intern.assignments.map(
           assignment=>
           <Card className={classes.card}>
            <CardContent>
              <Typography className={classes.title}>
                {assignment.task.taskName}
              </Typography>
              <Typography>
                <h2>Deadline: </h2>{assignment.task.deadline}
              </Typography>
            </CardContent>
           </Card>
         )
        }
      </Box>
    )
}
