import React,{Component} from 'react';
import { auth  } from './Login/firebase.js'
import {Container,AppBar,Button,Toolbar,Typography,List,ListItem,ListItemText,ListSubheader} from '@material-ui/core'


class AdminApp extends Component
{
    constructor(props)
    {
        super(props);
        this.state =
        {
          user:null,
          interns:[],
        }
        this.logout = ()=>{auth.signOut()}
    }
    componentWillMount()
    {
       this.setState({user:this.props.user});
    }

    async componentDidMount()
    {
        const response = await fetch('/api/interns/');
        const body = await response.json()
        this.setState({interns:body})
    }

    render()
    {
        const {interns,}=this.state
        return(
            <Container>
            <AppBar position="static">
                 <Toolbar>
                   <Typography type="title" color="inherit" style={{ flex: 1 }}>
                     Welcome {this.state.user.displayName}
                   </Typography>
                   <div>
                     <Button raised color="accent" onClick={this.logout}>
                       Logout
                     </Button>
                   </div>
                 </Toolbar>
               </AppBar>
               <div>
               {
                  interns.map(
                    intern=><List key={intern.id} subheader={<ListSubheader>{intern.name}</ListSubheader>}>
                        {intern.name}
                    </List>

                  )
               }
               </div>
             </Container>
               )
    }
}

export default AdminApp;
