import React,{useState} from 'react'
import {makeStyles} from '@material-ui/core/styles'
import {Card,CardContent,Typography, CardActions,IconButton,Collapse} from '@material-ui/core'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore'
import clsx from 'clsx'
const useStyles = makeStyles((theme)=>({
    card: {
        width:250,
        display: 'block',
        margin:8,
        transitionDuration: '0.3s',
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    content:{
        display:'block',
        overflow:'hidden'
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
          duration: theme.transitions.duration.shortest,
        }),
      },
    expandOpen: {
        transform: 'rotate(180deg)',
    }
  }));
export default function Task(props)
{
    const [expanded,setExpanded] = useState(false);
    const classes = useStyles();
    const handleExpandClick = () => {
        setExpanded(!expanded);
      };
    return(
        <Card className={classes.card}>
            <CardContent>
                <Typography className={classes.title}>
                    {props.title}
                </Typography>
                <Typography className={classes.title}>Deadline: {props.deadline}</Typography>
            </CardContent>
            <CardActions>
                <Typography className={classes.bullet}>
                    <FormControl className={classes.formControl}>
                          <InputLabel htmlFor="age-simple">Age</InputLabel>
                            <Select
                              value={values.age}
                              onChange={handleChange}
                              inputProps={{
                              name: 'age',
                              id: 'age-simple',
                              }}
                              >
                            <MenuItem value={10}>Ten</MenuItem>
                            <MenuItem value={20}>Twenty</MenuItem>
                            <MenuItem value={30}>Thirty</MenuItem>
                          </Select>
                    </FormControl>
                </Typography>
                <IconButton
                    className={clsx(classes.expand, {
                        [classes.expandOpen]: expanded,
                      })}
                    onClick={handleExpandClick}
                    aria-expanded={expanded}
                    aria-label="show more">
                    <ExpandMoreIcon />
                </IconButton>
            </CardActions>
            <Collapse in={expanded} timeout="auto" unmountOnExit>
                <CardContent>
                    <Typography className={classes.content}>Description: {props.description}</Typography>
                </CardContent>
            </Collapse>
        </Card>
    );
}
