const initialState = {
    taskList:[],
    searchTerm: ""
}

export default (state = initialState, action) =>
{
    switch(action.type)
    {
        case "UPDATE_DATA":
            return {
                ...state,
                taskList:action.payload
            }
        case "CHANGE_TERM":
            return {
                ...state,
                taskList:state.taskList.filter(value=>value.contains(action.payload)),
                searchTerm: action.payload
            };
        default:
            return state
    }
}
