import React,{useState,useEffect} from 'react';
import { CardContent,Card,Box,Typography, Container,Toolbar,AppBar,InputBase,Button } from '@material-ui/core';
import SearchIcon from '@material-ui/icons/Search'
import {makeStyles,fade} from '@material-ui/core/styles'
import Task from './Models/Task'
const useStyles = makeStyles(theme=>({
    pos: {
      marginBottom: 12,
    },
    root:
    {
        display:'flex',
        flexWrap:'wrap',
        flexDirection:'row',
        alignItems:'center',
    },
    search: 
    {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
          backgroundColor: fade(theme.palette.common.white, 0.25),
          },
        marginRight: theme.spacing(2),
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
          marginLeft: theme.spacing(3),
          width: 'auto',
        },
      },
      searchIcon: {
        width: theme.spacing(7),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      },
      inputRoot: {
        color: 'inherit',
      },
      tools:
      {
        flexGrow:1,
        display:'flex'
      },
      inputInput: {
        padding: theme.spacing(1, 1, 1, 7),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('md')]: {
          width: 200,
        },
      }
  }));
export default function InternApp(props)
{
    const [searchTerm,setSearchTerm] = useState("")
    const [taskList,setTaskList] = useState(props.intern.assignments)


    console.log(props.intern)
    const classes = useStyles();
    return(
      <Container maxWidth="sm">
      <AppBar>
        <Toolbar>
          <Typography className={classes.title} variant="h6" noWrap>
            Intern Application
          </Typography>
          <div className={classes.tools}>
            <div className={classes.search}>
              <div className={classes.searchIcon}>
                <SearchIcon />
              </div>
              <InputBase
                placeholder="Search…"
                classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
                }}
                inputProps={{ 'aria-label': 'search' }}

              />
            </div>
          </div>
          <Button color="inherit">Login</Button>
        </Toolbar>
      </AppBar>
      <Toolbar></Toolbar>
      <Box className = {classes.root}>
        {
         taskList.map(
           assignment=>
           <Task key={assignment.id} title={assignment.task.taskName} description={assignment.task.description} deadline={assignment.task.deadline} status={assignment.status} />
         )
        }
      </Box>
      </Container>
    )
}
